\textit{[University of Aberystwyth, Wales 2001]}

\section*{Solution}

\begin{enumerate}
    \item \textbf{Inductive and Capacitive Reactance:}
    \begin{align*}
        X_L &= \omega L, \\
        X_C &= \frac{1}{\omega C},
    \end{align*}
    where $\omega$ is the angular frequency of the AC source.

    \item \textbf{Total Impedance:}
    \begin{align*}
        Z &= \sqrt{R^2 + (X_L - X_C)^2}.
    \end{align*}
    \begin{center}
