    \begin{align*}
        I_T &= \frac{V}{Z},
    \end{align*}
    where $V$ is the supply voltage.

    \item \textbf{Phase Angle:}
    \begin{align*}
        \phi &= \tan^{-1}\left(\frac{X_L - X_C}{R}\right).
    \end{align*}

    \item \textbf{Voltages Across $R$, $C$, and $L$:}
    \begin{align*}
        V_R &= I_T R, \\
        V_C &= I_T X_C, \\
        V_L &= I_T X_L.
    \end{align*}
    \begin{center}
        \includegraphics[width=0.5\textwidth]{voltage_phasor_diagram.png}
    \end{center}

    \item \textbf{Condition for Resonance:}
    \begin{align*}
        X_L &= X_C \quad \Rightarrow \quad \omega L = \frac{1}{\omega C}, \\
        \omega_0 &= \frac{1}{\sqrt{LC}}.
    \end{align*}
