\section*{2.1 (a)}

\textbf{Acceleration:}

\begin{equation}
\text{Acceleration} = \frac{\text{Force}}{\text{Total mass}}
\end{equation}

\begin{equation}
a = \frac{F}{m_1 + m_2 + m_3}
\end{equation}

