\end{equation}

\begin{equation}
    V = mgl(1-\cos\theta)
\end{equation}

\begin{equation}
    L = T - V = \frac{1}{2}ml^2\dot{\theta}^2 - mgl(1-\cos\theta)
\end{equation}

\begin{equation}
    \frac{\partial T}{\partial \dot{\theta}} = ml^2\dot{\theta}, \quad \frac{\partial T}{\partial \theta} = 0
\end{equation}

\begin{equation}
    \frac{\partial V}{\partial \dot{\theta}} = 0, \quad \frac{\partial V}{\partial \theta} = mgl\sin\theta
\end{equation}

The Euler-Lagrange equation is given by:

\begin{equation}
    \frac{d}{dt} \left( \frac{\partial L}{\partial \dot{\theta}} \right) - \frac{\partial L}{\partial \theta} = 0
\end{equation}

Substituting $L = T - V$ gives:

\begin{equation}
    \frac{d}{dt} \left( \frac{\partial}{\partial \dot{\theta}} (T - V) \right) - \frac{\partial}{\partial \theta} (T - V) = 0
\end{equation}

This simplifies to:

\begin{equation}
    \frac{d}{dt}(ml^2\dot{\theta}) + mgl\sin\theta = 0
\end{equation}

or

\begin{equation}
    l\ddot{\theta} + g\sin\theta = 0 \quad \text{(equation of motion)}
\end{equation}

For small oscillation angles, $\sin\theta \approx \theta$, leading to:

\begin{equation}
    \ddot{\theta} = -\frac{g}{l}\theta \quad \text{(equation for angular SHM)}
\end{equation}

Thus, the angular frequency $\omega$ is:

\begin{equation}
    \omega^2 = \frac{g}{l}
\end{equation}

The time period $T$ is given by:

\begin{equation}
    T = 2\pi \omega = 2\pi \sqrt{\frac{l}{g}}
\end{equation}

